const rock=document.getElementById("rock"),paper=document.getElementById("paper"),scissor=document.getElementById("scissor"),rockIcon="✊",paperIcon="✋",scissorIcon="✌️";let roundWinner="",playerScore=0,computerScore=0;rock.addEventListener("click",(()=>game("rock",rockIcon))),paper.addEventListener("click",(()=>game("paper",paperIcon))),scissor.addEventListener("click",(()=>game("scissor",scissorIcon)));const playerPoint=document.getElementById("playerScore"),computerPoint=document.getElementById("computerScore"),result=document.querySelector(".result"),info=document.querySelector(".info"),playerSign=document.getElementById("player-sign"),computerSign=document.getElementById("computer-sign");function game(e,r){if(5==playerScore||5==computerScore)return rock.removeEventListener("click",(()=>game("rock"))),paper.removeEventListener("click",(()=>game("paper"))),playerScore=0,void(computerScore=0);playerSign.textContent=r;playRound(e,getComputerChoice());playerPoint.textContent="Player: "+playerScore,computerPoint.textContent="Computer: "+computerScore,5!=playerScore?5==computerScore&&alert("computer wins"):alert("player wins")}function getComputerChoice(){let e=Math.floor(3*Math.random()+1);return 1==e?(e="rock",computerSign.textContent=rockIcon):2==e?(e="paper",computerSign.textContent=paperIcon):(e="scissor",computerSign.textContent=scissorIcon),e}function playRound(e,r){let o="",t="";return e===r&&(roundWinner="tie",o="Game tied",t="Tie"),("rock"===e&&"scissor"===r||"scissor"===e&&"paper"===r||"paper"===e&&"rock"===r)&&(playerScore++,roundWinner="player",o="You Won",t=`${e.toUpperCase()} beats ${r.toUpperCase()}`),("rock"===r&&"scissor"===e||"scissor"===r&&"paper"===e||"paper"===r&&"rock"===e)&&(computerScore++,roundWinner="computer",o="You Lost",t=`${r.toUpperCase()} beats ${e.toUpperCase()}`),result.textContent=o,info.textContent=t,roundWinner}